                               AND bav.objectid = _objectid
                               AND bav.channelid = mc.channelid
                 LEFT JOIN channellookup clk ON clk.id = mc.channelid
        WHERE mc.menuid = _menuid;
    --AND mc.channelid = ANY(_channelids);
END;
$$;


