    LANGUAGE plpgsql
    AS $$
BEGIN
RETURN QUERY
select  ad.id ,cad.name , ad.subareaname ,
getlocalizationvalue('ar' , 'subareaName' ,ad.subareaname , 1),
ad.areaname, getlocalizationvalue('ar' , 'areaName' ,ad.areaname , 1), 
