    LANGUAGE plpgsql
    AS $$
BEGIN
RETURN QUERY
select
    count(rc1.customerid) totalcount,
    ceil(count(rc1.customerid)/ _pagesize::decimal)::bigint pagecount,
    to_json(Array( select to_json(json_build_object(
                                  'customerid', rc.customerid,
