                                  'phonenumber',cd.phonenumber,
                                  'currentpoints',rc.currentpoints,
                                  'lastpointdate',(rc.lastpointdate ::date),
                                  --'currentstatus',rc.currentstatus,
                                  'lookupname',(select coalesce( rl.rname, l.name) from lookup l
