    LANGUAGE plpgsql
    AS $$
begin
IF((Select nlk.name from namelookup nlk WHERE nlk.id = _channelid and nlk.tabletype ='ordersource') != 'callcenter')
THEN
delete from menuintegratiosync mis
WHERE mis.channelid NOT IN (
   SELECT mc.channelid
